<?phpnamespace app\controllers;use Yii;use yii\web\Controller;use app\models\RegistrationForm; use app\models\User;class RegistrationController extends Controller{        public function actionIndex()    {              $model = new RegistrationForm();        return $this->render('index', ['model' => $model]);    }        public function actionRegister()     {        $model = new RegistrationForm();        $form = Yii::$app->request->post('RegistrationForm');               $user = new User();                            $model->attributes = $form;                   $email = $user->checkEmail($model->email);        if ($model->validate()){            if ($email["count"] == 0){                                $user->addUser($model);                 return $this->redirect('http://masterillla.com/web/index.php?r=registration/success');             } else {                  return $this->redirect('http://masterillla.com/web/index.php?r=registration/error');              }        }else {            return $this->redirect('http://masterillla.com/web/index.php?r=registration/error');          }              }     public function actionSuccess()    {        return $this->render('success');    }         public function actionError()    {        return $this->render('error');    }        }