<?phpnamespace app\models;use yii\base\Model;class RegisterForm extends Model{    public $email;    public $password;    public $password2;    public $firstname;    public $lastname;    public $country;    public $region;    public $city;        public function rule()    {        return [            [['email', 'password', 'password2', 'firstname', 'lastname', 'country', 'region', 'city'], 'required'],            ['email', 'email'],            ['password', 'validatePassword'],        ];    }        public function validatePassword($attribute, $params)    {        if (!$this->hasErrors()) {            $user = $this->getUser();            if (!$user || !$user->validatePassword($this->password)) {                $this->addError($attribute, 'Incorrect username or password.');            }        }    }    }